    $('.muteSong').click(function() {
        if( $("audio").prop('muted') ) {
            $("audio").prop('muted', false);
        } else {
            $("audio").prop('muted', true);
        }
    });

    $("#videoSpinner").bind("ended", function() {
        window.location.replace("resultado.php");
    });

    $("#videoSpinnerMob").bind("ended", function() {
        window.location.replace("resultado.php");
    });

    $('.imgAudio').on({
        'click': function(){
            if($('.imgAudio').attr("src") == 'assets/images/audio.svg'){
                $('.imgAudio').attr('src','assets/images/audio_no.svg');
            } else {
                $('.imgAudio').attr('src','assets/images/audio.svg');
            }
        }
    });
